import React from "react";
import styles from "../styles.module.css";

type IProps = {
  item: any;
};

const ChatItem: React.FC<IProps> = ({ item }) => {
  return (
    <div className={`${styles.chatItem} ${item.fromMe ? styles.right : ""}`}>
      {item.message}
    </div>
  );
};

export default ChatItem;
